import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import { useContext, useEffect, useState } from "react";

//import "bootstrap/dist/css/bootstrap.min.css";
import AppContext from "./context";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Container,
  Row,
  Col,
} from "reactstrap";

function fetchDish() {
  const GET_RESTAURANT_DISHES = gql`
    query ($id: ID!) {
      restaurant(id: $id) {
        name
        dishes {
          id
          name
          description
          price
        }
      }
    }
  `;

  const { loading, error, data } = useQuery(GET_RESTAURANT_DISHES, {
    variables: { id: restId },
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR here</p>;
  if (!data) return <p>Not found</p>;

  let restaurant = data.restaurant;
  console.log("inside fetch dishes, restaurant:");
  console.log(restaurant);
}

function RestaurantList(props) {
  const [restaurantID, setRestaurantID] = useState(0);
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const [showDishes, setShowDishes] = useState(false);
  const { addItem } = useContext(AppContext);

  useEffect(() => {
    setShowDishes(true);
  }, [restaurantID]);

  const GET_RESTAURANTS = gql`
    query Restaurants {
      restaurants {
        id
        name
        description
        dishes {
          name
          price
          description
          id
        }
      }
    }
  `;

  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR</p>;
  if (!data) return <p>Not found</p>;
  console.log(`Query Data:`);
  console.log(data.restaurants);

  let searchQuery =
    data.restaurants.filter((res) => {
      return res.name.toLowerCase().includes(props.search);
    }) || [];

  function DishesList() {
    if (restaurantID) {
      console.log("data.restaurants:");
      console.log(data.restaurants);
      let selectedRes = data.restaurants.filter(
        (rest) => rest.id === restaurantID
      );
      console.log("selectedRes");
      console.log(selectedRes);

      let restName = selectedRes[0].name;
      console.log("restName");
      console.log(restName);
      let dishArray = selectedRes[0].dishes;

      console.log("dishArray");
      console.log(dishArray);
      const mapThroughDishes = dishArray.map((dish) => (
        <Col xs="6" sm="4" style={{ padding: 0 }} key={dish.id}>
          <Card style={{ margin: "0 10px" }}>
            {/* <CardImg
              top={true}
              style={{ height: 150, width: 150 }}
              src={`http://localhost:1337${res.image.url}`}
            /> */}
            <CardBody>
              <CardTitle>{dish.name}</CardTitle>
              <CardText>${dish.price}</CardText>
              <CardText>{dish.description}</CardText>
            </CardBody>
            <div className="card-footer">
              <Button
                // color="info"
                outline
                color="primary"
                onClick={() => addItem(dish)}
              >
                + Add To Cart
              </Button>
            </div>
          </Card>
        </Col>
      ));

      return (
        <div>
          <br /> <h4>{restName} Menu</h4>
          <br />
          {mapThroughDishes}
        </div>
      );
    } else {
      return <div></div>;
    }
  }

  if (searchQuery.length > 0) {
    const restList = searchQuery.map((res) => (
      <Col xs="6" sm="4" key={res.id}>
        <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
          {/* <CardImg
            top={true}
            style={{ height: 200 }}
            src={`http://localhost:1337` + res.image.url}
          /> */}
          <CardBody>
            <CardTitle tag="h5">{res.name}</CardTitle>
            <CardText>{res.description}</CardText>
          </CardBody>
          <div className="card-footer">
            <Button
              color="info"
              onClick={() => {
                setShowDishes(false);
                setRestaurantID(res.id);
              }}
            >
              Explore
            </Button>
          </div>
        </Card>
      </Col>
    ));

    return (
      <Container>
        <Row xs="3">{restList}</Row>
        <DishesList />
      </Container>
    );
  } else {
    return <h1> No Restaurants Found</h1>;
  }
}

export default RestaurantList;
