import { gql, useQuery } from "@apollo/client";
import Dishes from "./dishes";
import { useContext, useEffect, useState } from "react";

//import "bootstrap/dist/css/bootstrap.min.css";
import AppContext from "./context";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Container,
  Row,
  Col,
} from "reactstrap";

function RestaurantList(props) {
  const [restaurantID, setRestaurantID] = useState(0);
  const { cart } = useContext(AppContext);
  const [state, setState] = useState(cart);
  const [showDishes, setShowDishes] = useState(false);

  const GET_RESTAURANTS = gql`
    query Restaurants {
      restaurants {
        id
        name
        description
        dishes {
          name
          price
          description
        }
      }
    }
  `;

  const { loading, error, data } = useQuery(GET_RESTAURANTS);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR</p>;
  if (!data) return <p>Not found</p>;
  console.log(`Query Data:`);
  console.log(data.restaurants);

  let searchQuery =
    data.restaurants.filter((res) => {
      return res.name.toLowerCase().includes(props.search);
    }) || [];

  useEffect(() => {
    console.log("HEYYA!!");
  }, [restaurantID]);
  ////
  let dishArray = [];
  function findDishes(id) {
    let selectedRestaurant = data.restaurants.filter((res) => res.id === id);
    dishArray = selectedRestaurant[0].dishes;
    console.log("dishArray");
    console.log(dishArray);
  }

  function DishesList() {
    return dishArray.map((dish) => {
      console.log(dish.name);
      return (
        <Card>
          <CardBody>
            <CardTitle>{dish.name}</CardTitle>
          </CardBody>
        </Card>
      );
    });
  }

  if (searchQuery.length > 0) {
    const restList = searchQuery.map((res) => (
      <Col xs="6" sm="4" key={res.id}>
        <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
          {/* <CardImg
            top={true}
            style={{ height: 200 }}
            src={`http://localhost:1337` + res.image.url}
          /> */}
          <CardBody>
            <CardTitle tag="h5">{res.name}</CardTitle>
            <CardText>{res.description}</CardText>
          </CardBody>
          <div className="card-footer">
            <Button
              color="info"
              onClick={() => {
                setRestaurantID(res.id);
                console.log(res.id);
                console.log(restaurantID);
                findDishes(res.id);
                setShowDishes(true);
              }}
            >
              Explore
            </Button>
          </div>
        </Card>
      </Col>
    ));

    return (
      <Container>
        <Row xs="3">{restList}</Row>
        {/* 
        <Row xs="3">{renderDishes(restaurantID)}</Row>
        <Row xs="3">{renderDishes(restId)}</Row> */}
        {showDishes && <DishesList />}
      </Container>
    );
  } else {
    return <h1> No Restaurants Found</h1>;
  }
}

export default RestaurantList;
